import React, { useState } from "react";
import { CSVLink } from "react-csv";

const Roles = () => {
  // Static roles data
  const [roles, setRoles] = useState([
    { roleName: "konkati.sravya@beeinnovations.com", status: "Active" },
    { roleName: "satish@mm", status: "Active" },
    { roleName: "md.abdul@beeinnovations.com", status: "Active" },
    { roleName: "rjsuneetha@beeinnovations.com", status: "Active" },
    { roleName: "nirisha.g@beeinnovations.com", status: "Active" },
  ]);

  const [searchTerm, setSearchTerm] = useState("");

  // Filter roles based on search term
  const filteredRoles = roles.filter((role) =>
    role.roleName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // CSV headers and data
  const csvHeaders = [
    { label: "Role Name", key: "roleName" },
    { label: "Status", key: "status" },
  ];

  const csvData = filteredRoles.map((role) => ({
    roleName: role.roleName,
    status: role.status,
  }));

  // Delete Role
  const handleDelete = (index) => {
    const updatedRoles = roles.filter((_, i) => i !== index);
    setRoles(updatedRoles);
  };

  return (

      <div className="row m-auto p-auto">
        <div className="col">
          <div className="d-flex justify-content-between align-items-center m-3">
            <h2>Roles</h2>
            <div>
              <button
                className="btn btn-primary me-2"
                onClick={() => alert("Add Role clicked!")}
              >
                Add Role
              </button>
              {/* CSV Download Button */}
            </div>
          </div>
          <div className="d-flex  justify-content-between">
            {/* Search bar */}

            <CSVLink
              data={csvData}
              headers={csvHeaders}
              filename={"roles.csv"}
              className="btn btn-secondary"
            >
              Download CSV
            </CSVLink>

            <input
              type="text"
              className="form-control w-25"
              placeholder="Search by role name"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <div
            className="table-responsive rounded shadow "
            style={{ height: "60vh", overflowY: "auto" }}
          >
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>S.No</th>
                  <th>Role Name</th>
                  <th>Status</th>
                  <th>Action</th>

                  <th>Permissions</th>
                </tr>
              </thead>
              <tbody>
                {filteredRoles.map((role, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{role.roleName}</td>
                    <td>{role.status}</td>
                    <td>
                      <span>
                        <i
                          class="bi bi-trash m-1 text-dark"
                          onClick={() => handleDelete(index)}
                        ></i>
                      </span>
                    </td>
                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() => alert("Permissions clicked!")}
                      >
                        View Permissions
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

  );
};

export default Roles;
