import React, { useState, useEffect } from 'react';
import ReactTable from 'react-table-6';
import 'react-table-6/react-table.css';
import CSVExport from 'react-json-to-csv';


const Posts = () => {


  const samplePosts = [
    {
      postid: 1,
      categoryname: 'Sample Category 1',
      posttitle: 'Sample Post 1',
      rssname: 'Sample RSS 1',
      status: 'Publish',
      created_at: '2023-09-21 10:30:00',
      published_date: '2023-09-21 11:45:00',
      updated_at: '2023-09-21 12:15:00',
      postlink: 'sample-post-1',
    },
    {
      postid: 2,
      categoryname: 'Sample Category 2',
      posttitle: 'Sample Post 2',
      rssname: 'Sample RSS 2',
      status: 'Pending',
      created_at: '2023-09-20 09:15:00',
      published_date: '2023-09-21 12:15:00',
      updated_at: '2023-09-20 10:30:00',
      postlink: 'sample-post-2',
    },
    {
      postid: 3,
      categoryname: 'Sample Category 2',
      posttitle: 'Sample Post 2',
      rssname: 'Sample RSS 2',
      status: 'Pending',
      created_at: '2023-09-20 09:15:00',
      published_date: '2023-09-21 12:15:00',
      updated_at: '2023-09-20 10:30:00',
      postlink: 'sample-post-2',
    },
    {
      postid: 4,
      categoryname: 'Sample Category 2',
      posttitle: 'Sample Post 2',
      rssname: 'Sample RSS 2',
      status: 'Pending',
      created_at: '2023-09-20 09:15:00',
      published_date: '2023-09-21 12:15:00',
      updated_at: '2023-09-20 10:30:00',
      postlink: 'sample-post-2',
    },
    {
      postid: 5,
      categoryname: 'Sample Category 2',
      posttitle: 'Sample Post 2',
      rssname: 'Sample RSS 2',
      status: 'Pending',
      created_at: '2023-09-20 09:15:00',
      published_date: '2023-09-21 12:15:00',
      updated_at: '2023-09-20 10:30:00',
      postlink: 'sample-post-2',
    },
    {
      postid: 6,
      categoryname: 'Sample Category 2',
      posttitle: 'Sample Post 2',
      rssname: 'Sample RSS 2',
      status: 'Pending',
      created_at: '2023-09-20 09:15:00',
      published_date: '2023-09-21 12:15:00',
      updated_at: '2023-09-20 10:30:00',
      postlink: 'sample-post-2',
    },

    // Add more sample data as needed
  ];

  const columns = [
    {
      Header: 'S.No',
      accessor: 'postid',
    },
    {
      Header: 'Category Name',
      accessor: 'categoryname',
    },
    {
      Header: 'Post Title',
      accessor: 'posttitle',
    },
    {
      Header: 'RSS Name',
      accessor: 'rssname',
    },
    {
      Header: 'Status',
      accessor: 'status',
      Cell: ({ value }) => (
        <span className={`badge badge-pill ${value === 'Publish' ? 'badge-success' : 'badge-danger'}`}>
          {value}
        </span>
      ),
    },
    {
      Header: 'Received on',
      accessor: 'created_at',
    },
    {
      Header: 'Published on',
      accessor: 'published_date',
    },
    {
      Header: 'Edited on',
      accessor: 'updated_at',
    },
    {
      Header: 'View',
      accessor: 'postlink',
      Cell: ({ value, row }) => (
        <a
          href={row.original.status === 'Publish' ? `post-single&id=${value}` : `admin-post-single&id=${value}`}
          className="btn btn-success"
          target="_blank"
          rel="noopener noreferrer"
        >
          <i className="fa fa-eye" aria-hidden="true"></i>
        </a>
      ),
    },
    {
      Header: 'Action',
      accessor: 'postid',
      Cell: ({ value }) => (
        // Render your edit and delete actions here
        // You can use value (postid) to perform actions on this specific post
        <div>
          {/* Example: */}
          <button>Edit</button>
          <button>Delete</button>
        </div>
      ),
    },
  ];


  const exportToCSV = () => {
    const csvData = samplePosts.map((post) => ({
      'S.No': post.postid,
      'Category Name': post.categoryname,
      'Post Title': post.posttitle,
      'RSS Name': post.rssname,
      'Status': post.status,
      'Received on': post.created_at,
      'Published on': post.published_date,
      'Edited on': post.updated_at,
      'View': original.status === 'Publish' ? `post-single&id=${post.postlink}` : `admin-post-single&id=${post.postlink}`,
    }));

    const csvContent = "data:text/csv;charset=utf-8," + 
      Object.keys(csvData[0]).map(key => key).join(",") + "\n" +
      csvData.map(post => Object.values(post).join(",")).join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "posts_data.csv");
    document.body.appendChild(link); // Required for FF

    link.click(); // Initiates the download
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="d-flex justify-content-between mt-5 px-4">
          <h4>Posts</h4>
          <button className="btn btn-info text-light">Add New</button>
        </div>

        <div className="content-page">
          <div className="content">
            <div className="container-fluid">
              <div className="page-title-box pt-2">
                {/* ... (page title and buttons) */}
              </div>
              <div className="row">
                <div className="col-12">
                  <div className="card">
                    <div className="card-body">
                      <div className="row bg-secondary p-2 m-2">
                        <div className="col">
                          <h5 className="text-center">Filters:</h5>
                        </div>
                        <div className="form-group col">
                          <select id="inputState1" className="form-control">
                            <option selected>Choose...</option>
                            <option>Fashion</option>
                          </select>
                        </div>

                        <div className="col form-group">
                          <select id="inputState2" className="form-control">
                            <option selected>Choose...</option>
                            <option>Fashion</option>
                          </select>
                        </div>

                        <div className="col form-group">
                          <select id="inputState3" className="form-control">
                            <option selected>Choose...</option>
                            <option>Fashion</option>
                          </select>
                        </div>

                        <button className="btn btn-warning text-dark col sm">Submit</button>
                      </div>

                      <div className="col-md-12 mb-3">
                        <button className="btn btn-info text-light">
                          <i className="fas fa-globe-americas"></i> Publish
                        </button>
                      </div>
                      <div className="postsrow">
                        <ReactTable
                          data={samplePosts}
                          columns={columns}
                          pageSizeOptions={[5, 10, 20, 25, 50]}
                          defaultPageSize={10}
                          filterable={true}
                          className="-striped -highlight"
                        />
                        <div className="mt-3">
                          <CSVExport data={samplePosts} filename="posts_data.csv">
                            <button className="btn btn-info text-light">
                              <i className="fas fa-file-csv"></i> Export CSV
                            </button>
                          </CSVExport>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Posts;
