import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './Components/Sidebar/Sidebar';
import Navbar from './Components/Navbar/Navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import Dashboard from './Components/Dashboard/Dashboard';
import Posts from './Components/Posts/Posts';
import Addpost from './Components/Posts/Addpost';
import Editposts from './Components/Posts/Editposts';
import Paparazzi from './Components/Paparazzi/Paparazzi';
import Login from './Components/Login/Login';
import Category from './Components/Category/Category';
import Addcategory from './Components/Category/Addcategory';
import EditCategory from './Components/Category/EditCategory';
import { AuthProvider, useAuth } from './Context/AuthContext';
import './App.css';

function App() {
  // State to manage sidebar visibility
  const [isSidebarVisible, setIsSidebarVisible] = useState(false);

  // Toggle function for showing and hiding the sidebar
  const toggleSidebar = () => {
    setIsSidebarVisible(!isSidebarVisible);
  };

  return (
    <AuthProvider>
      <Router>
        <div className="d-flex vh-100 p-0 m-0">
          <AuthenticatedContent isSidebarVisible={isSidebarVisible} toggleSidebar={toggleSidebar} />
        </div>
      </Router>
    </AuthProvider>
  );
}

const ProtectedRoute = ({ element }) => {
  const { isLoggedIn } = useAuth();
  return isLoggedIn ? element : <Navigate to="/" />;
};

const AuthenticatedContent = ({ isSidebarVisible, toggleSidebar }) => {
  const { isLoggedIn } = useAuth();

  return (
    <>
      {isLoggedIn && (
        <div className={isSidebarVisible ? "sidebar-shown" : "sidebar-hidden"}>
          <Sidebar toggle={toggleSidebar} />
        </div>
      )}
      <div className={`main-content col-lg col-md col-sm overflow-auto`}>
        {isLoggedIn && <Navbar toggle={toggleSidebar} />}
        <Routes>
          {isLoggedIn ? (
            <>
              <Route path="/dashboard" element={<ProtectedRoute element={<Dashboard />} />} />
              <Route path="/Posts" element={<ProtectedRoute element={<Posts />} />} />
              <Route path="/addpost" element={<ProtectedRoute element={<Addpost />} />} />
              <Route path="/editposts" element={<ProtectedRoute element={<Editposts />} />} />
              <Route path="/paparazzi" element={<ProtectedRoute element={<Paparazzi />} />} />
              <Route path="/category" element={<ProtectedRoute element={<Category />} />} />
              <Route path="/addcategory" element={<ProtectedRoute element={<Addcategory />} />} />
              <Route path="/editcategory" element={<ProtectedRoute element={<EditCategory />} />} />
            </>
          ) : (
            <Route path="/" element={<Login />} />
          )}
        </Routes>
      </div>
    </>
  );
};

export default App;
