import QuillToolbar, { modules, formats } from "./EditorToolbar"; 

import { useNavigate, useLocation } from "react-router-dom";


const Posts1 = () => {
  const [formData, setFormData] = useState({
    categoryName: "",
    postTitle: "",
    contentType: "",
    users: "",
    trendingNow: false,
    hotContent: false,
    sendGmail: false,
    schedulePost: "",
    language: "",
    hashtag: "",
    status: "",
    author: "",
    description: "",
    videoPath: "", 
  });
  const [imageFile, setImageFile] = useState(null); // To handle image file

  const [alert, setAlert] = useState({ show: false, message: "", type: "" });
  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Store the selected file
  };
  const location = useLocation();
  const { categories } = location.state || {};
const Navigate =useNavigate();
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleQuillChange = (value) => {
    setFormData({
      ...formData,
      description: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append("categoryName", formData.categoryName);
    data.append("postTitle", formData.postTitle);
    data.append("contentType", formData.contentType);
    data.append("users", formData.users);
    data.append("trendingNow", formData.trendingNow);
    data.append("hotContent", formData.hotContent);
    data.append("sendGmail", formData.sendGmail);
    data.append("schedulePost", formData.schedulePost);
    data.append("language", formData.language);
    data.append("hashtag", formData.hashtag);
    data.append("status", formData.status);
    data.append("description", formData.description);
    data.append("videoPath", formData.videoPath); // Append video URL
    if (imageFile) {
      data.append("image", imageFile); // Append image file if selected
    }

    // const jsonObject = {};
    // data.forEach((value, key) => {
    //   jsonObject[key] = value;
    // });

    // Print the object in JSON format
    // console.log(JSON.stringify(jsonObject)); // Pretty print JSON


    // Convert FormData to a JSON-like object
const jsonObject = Object.fromEntries(data.entries());

// // Print the object in JSON format
// console.log(JSON.stringify(jsonObject, null, 2));
  console.log(JSON.stringify(jsonObject));
    fetch('http://172.22.9.88:9966/mm/articles', {
      method: 'POST',
      body: data, // Send FormData object
    })
    .then(response => response.json())
    .then(data => {
      setAlert({ show: true, message: data.message, type: "success" });
      setFormData({
        categoryName: "",
        postTitle: "",
        contentType: "",
        users: "",
        trendingNow: false,
        hotContent: false,
        sendGmail: false,
        schedulePost: "",
        language: "",
        hashtag: "",
        status: "",
        description: "",
      });
      setTimeout(() => {
        setAlert({ show: false, message: "", type: "" });
        Navigate("/Posts");
      }, 1000);
    })
    .catch((error) => {
      console.error('Error:', error);
      setAlert({
        show: true,
        message: "Error adding post. Please try again.",
        type: "danger",
      });
    });
  };



  const redirecttoposts=()=>{
    Navigate("/Posts");
  }

  const handleAlertClose = () => {
    setAlert({ show: false, message: "", type: "" });
    Navigate("/Posts");
  };

  return (
    <div className='container-fluid'>
      <div className='row'>
        <div className='d-flex justify-content-between mt-3'>
          <h4 className='fs-4'>Add Posts</h4>
          <button className='btn btn-outline-warning text-dark shadow px-3' onClick={redirecttoposts}>Posts</button>
        </div>
        {alert.show && (
          <div
            className={`alert alert-${alert.type} alert-dismissible  w-50 fade show m-auto p-auto`}
            role="alert"
          >
            {alert.message}
            <button
              type="button"
              className="btn-close"
              onClick={handleAlertClose}
            ></button>
          </div>
