const Posts1 = () => {

     const samplePosts = [
    {
      postid: 1,
      categoryname: 'Sample Category 1',
      posttitle: 'Sample Post 1',
      rssname: 'Sample RSS 1',
      status: 'Publish',
      created_at: '2023-09-21 10:30:00',
      published_date: '2023-09-21 11:45:00',
      updated_at: '2023-09-21 12:15:00',
      postlink: 'sample-post-1',
    },
    {
      postid: 2,
      categoryname: 'Sample Category 2',
      posttitle: 'Sample Post 2',
      rssname: 'Sample RSS 2',
      status: 'Pending',
      created_at: '2023-09-20 09:15:00',
      published_date: '',
      updated_at: '2023-09-20 10:30:00',
      postlink: 'sample-post-2',
    },
    // Add more sample data as needed
  ];
            const [userLoggedIn, setUserLoggedIn] = useState(false);
            const [roleId, setRoleId] = useState(null);
            const [rssAccess, setRssAccess] = useState('');
            const [postPermit, setPostPermit] = useState('');
            const [postPermitEdit, setPostPermitEdit] = useState('');
            const [postPermitDelete, setPostPermitDelete] = useState('');
            const [postPermitAdd, setPostPermitAdd] = useState('');
            const [filterCategoryName, setFilterCategoryName] = useState('');
            const [rssName, setRssName] = useState('');
            const [status, setStatus] = useState('');
            const [posts, setPosts] = useState([]);
            const [selectedPosts, setSelectedPosts] = useState([]);


            const handleFilterChange = (e) => {
              // Handle changes in filter values
              // Update state variables like filterCategoryName, rssName, and status
            };

            const handleFilterSubmit = (e) => {
              e.preventDefault();
              // Fetch filtered posts based on filterCategoryName, rssName, and status
            //   const filteredPosts = fetchFilteredPosts(); // Implement this function
              setPosts();
            };

            const handlePublish = () => {
              // Handle publishing selected posts
              // You can use selectedPosts state to determine which posts to publish
            };

            const handleCheckboxChange = (e) => {
              const postId = e.target.value;
              const isChecked = e.target.checked;

              // Update selectedPosts based on checkbox changes
              if (isChecked) {
                setSelectedPosts((prevSelectedPosts) => [...prevSelectedPosts, postId]);
              } else {
                setSelectedPosts((prevSelectedPosts) =>
                  prevSelectedPosts.filter((id) => id !== postId)
                );
              }
            };

  return (


<div className='container-fluid'>
           <div className='row'>

           <div className='d-flex justify-content-between  mt-5 px-4'>
           <h4>Posts</h4>
             <button className='btn btn-info text-light'>Add New</button>
           </div>



    <div className="content-page">
      <div className="content">
        <div className="container-fluid">
          <div className="page-title-box pt-2">


            {/* ... (page title and buttons) */}
          </div>
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">

           <div className='row'>
  <div className='col-lg bg-secondary d-flex  flex-wrap flex-lg-nowrap m-3 p-2'>
    <h5>Filters:</h5>

    <div className="form-group mx-5">
      <select id="inputState1" className="form-control ">
        <option selected>Choose...</option>
        <option>Fashion</option>
      </select>
    </div>

    <div className=" form-group mx-5 ">
      <select id="inputState2" className="form-control">
        <option selected>Choose...</option>
        <option>Fashion</option>
      </select>
    </div>

    <div className=" form-group mx-5">
      <select id="inputState3" className="form-control">
        <option selected>Choose...</option>
        <option>Fashion</option>
      </select>
    </div>


      <button className='btn btn-warning text-dark  flex-column'>Submit</button>

  </div>
</div>

                  {/* ... (alerts and filters) */}
                  <div className="col-md-12 mb-3">
                    <button className="btn btn-info text-light" onClick={handlePublish}>
                      <i className="fas fa-globe-americas"></i> Publish
                    </button>
                  </div>
                  <div className="postsrow">
                    <table className="table table-bordered dt-responsive">
                      <thead>
                        <tr>
                          <th></th>
                          <th>S.No</th>
                          <th>Category Name</th>
                          <th>Post Title</th>
                          <th>RSS Name</th>
                          <th>Status</th>
                          <th>Received on</th>
                          <th>Published on</th>
                          <th>Edited on</th>
                          <th>View</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {/* {posts.map((post, index) => (
                          <tr key={post.postid}>
