import React, { useState, useEffect } from 'react';
import ReactTable from 'react-table-6';
import 'react-table-6/react-table.css';
import { useNavigate } from 'react-router-dom';

const Posts = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('http://172.22.9.59:3036');
        const apiPosts = await response.json();

        // Map the API data to the format expected by the table
        const formattedPosts = apiPosts.map((post, index) => ({
          postid: post.postid,
          categoryname: post.categoryid ? post.categoryid : 'N/A', // Assuming categoryid needs to be mapped to categoryname
          posttitle: post.posttitle ? post.posttitle : 'Untitled', // Fallback if posttitle is null
          rssname: post.rssid ? post.rssid : 'N/A', // Assuming rssid needs to be mapped to rssname
          status: post.status ? post.status : 'Unknown', // Fallback if status is null
          created_at: post.created_at ? post.created_at : 'N/A',
          published_date: post.published_date ? post.published_date : 'N/A',
          updated_at: post.updated_at ? post.updated_at : 'N/A',
          postlink: post.postlink ? post.postlink : '#',
        }));
        setPosts(formattedPosts);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  const columns = [
    {
      Header: 'S.No',
      accessor: 'postid',
    },
    {
      Header: 'Category Name',
      accessor: 'categoryname',
    },
    {
      Header: 'Post Title',
      accessor: 'posttitle',
    },
    {
      Header: 'RSS Name',
      accessor: 'rssname',
    },
    {
      Header: 'Status',
      accessor: 'status',
      Cell: ({ value }) => (
        <div className={`badge ${value === 'Publish' ? 'bg-success' : 'bg-danger'}`}>
          {value}
        </div>
      ),
    },
    {
      Header: 'Received on',
      accessor: 'created_at',
    },
    {
      Header: 'Published on',
      accessor: 'published_date',
    },
    {
      Header: 'Edited on',
      accessor: 'updated_at',
    },
    {
      Header: 'View',
      accessor: 'postlink',
      Cell: ({ value }) => (
        <a href={value} target="_blank" rel="noopener noreferrer">
          <i className="bi bi-eye-slash"></i>
        </a>
      ),
    },
    {
      Header: 'Action',
      accessor: 'postid',
      Cell: ({ value }) => (
        <div>
          <button className="btn me-2"><i className="bi bi-pencil-square"></i></button>
          <button className="btn me-2"><i className="bi bi-trash3"></i></button>
        </div>
      ),
    },
  ];

  const navigate = useNavigate();

  const redirectToAdds = () => {
    navigate("/Posts1");
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="d-flex justify-content-between mt-5 px-4">
          <h4>Posts</h4>
          <button className="btn btn-info text-light" onClick={redirectToAdds}>Add New</button>
        </div>
        <div className="content-page">
          <div className="content">
            <div className="container-fluid">
              <div className="page-title-box pt-2">
                {/* ... (page title and buttons) */}
              </div>
              <div className="row">
                <div className="col-12">
                  <div className="card">
                    <div className="card-body">
                      <div className="row bg-info p-2 my-2">
                        <div className="col">
                          <h5 className="text-center">Filters:</h5>
                        </div>
                        <div className="form-group col">
                          <select id="inputState1" className="form-control">
                            <option defaultValue>Choose...</option>
                            <option>Fashion</option>
                          </select>
                        </div>
                        <div className="col form-group">
                          <select id="inputState2" className="form-control">
                            <option defaultValue>Choose...</option>
                            <option>Fashion</option>
                          </select>
                        </div>
                        <div className="col form-group">
                          <select id="inputState3" className="form-control">
                            <option defaultValue>Choose...</option>
                            <option>Fashion</option>
                          </select>
                        </div>
                        <button className="btn btn-warning text-dark col">Submit</button>
                      </div>
                      <div className="row my-3">
                        <div className="col-md-6">
                          <button className="btn btn-info text-light">
                            <i className="fas fa-file-csv"></i> Export CSV
                          </button>
                        </div>
                        <div className="col-md-4 text-right">
                          <div className="form-group">
                            <input
                              type="text"
                              placeholder="Search..."
                              className="form-control"
                              // Implement search functionality here
                            />
                          </div>
                        </div>
                      </div>
                      <div className="postsrow">
                        <ReactTable
                          data={posts}
                          columns={columns}
                          pageSizeOptions={[5, 10, 20, 25, 50]}
                          defaultPageSize={5}
                          className="-striped -highlight"
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Posts;
