import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './Components/Sidebar/Sidebar';
import Navbar from './Components/Navbar/Navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import Dashboard from './Components/Dashboard/Dashboard';
import Posts from './Components/Posts/Posts';
import Posts1 from './Components/Posts/Posts1';
import Home from './Home/Home';
import Login from './Components/Login/Login';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle login
  const handleLogin = () => {
    // You can add your login logic here, and if login is successful, set isLoggedIn to true.
    // For simplicity, let's assume the user is logged in on button click.
    setIsLoggedIn(!isLoggedIn);
  };

  // Function to handle logout
  const handleLogout = () => {
    // You can add your logout logic here, and if logout is successful, set isLoggedIn to false.
    // Navigate(to="/login")
    setIsLoggedIn(!isLoggedIn);

  };


    const [toggleed,setToggleed]=useState(false);
  function  toggle() {
   setToggleed(!toggleed);
  }

  return (
    <Router>
      <div className="d-flex">
        {isLoggedIn && (
          <div className={ toggleed ? "d-none" :"w-auto"}>
            <Sidebar  />


          </div>
        )}
        <div className="col overflow-auto">
        {isLoggedIn && (
          <div >

            <Navbar handleLogout={handleLogout}  toggle={toggle}/>

          </div>
        )}
          <Routes>
            {isLoggedIn ? (

              <>
                <Route path="/" element={<Navigate to="/Dashboard" />} />
                <Route path="/Dashboard" element={<Dashboard />} />
                <Route path="/Posts" element={<Posts />} />
                <Route path="/Posts1" element={<Posts1 />} />
                <Route path="/Home" element={<Home />} />
              </>
            ) : (
              <Route
                path="/"
                element={
                  <Login
                    handleLogin={handleLogin}
                    // You can pass additional props or functions needed for login here
                  />
                }
              />
            )}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
