import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './Components/Sidebar/Sidebar';
import Navbar from './Components/Navbar/Navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import Dashboard from './Components/Dashboard/Dashboard';
import Posts from './Components/Posts/Posts';
import Posts1 from './Components/Posts/Posts1';
import Videos from './Components/Videos/Videos';
import Login from './Components/Login/Login';
import ProtectedRoute from './Components/ProtectedRoute';
import { AuthProvider, useAuth } from './Context/Authcontext';
import './App.css';

function App() {
  const [toggleed, setToggleed] = useState(false);

  const toggle = () => {
    setToggleed(!toggleed);
  };

  return (
    <AuthProvider>
      <Router>
        <div className="d-flex vh-100 p-0 m-0">
          <AuthenticatedContent toggleed={toggleed} toggle={toggle} />
        </div>
      </Router>
    </AuthProvider>
  );
}

const AuthenticatedContent = ({ toggleed, toggle }) => {
  const { isLoggedIn } = useAuth();

  return (
    <>
      {isLoggedIn && (
        <div className={toggleed ? "d-none" : "w-auto h-auto"}>
          <Sidebar />
        </div>
      )}
      <div className="col-lg col-md col-sm overflow-auto">
        {isLoggedIn && (
          <div>
            <Navbar toggle={toggle} />
          </div>
        )}
        <Routes>
          {isLoggedIn ? (
            <>
              <Route path="/Dashboard" element={<Dashboard />} />
              <Route path="/Posts" element={<Posts />} />
              <Route path="/Posts1" element={<Posts1 />} />
              <Route path="/Videos" element={<Videos />} />
              <Route path="/" element={<Navigate to="/Dashboard" />} />
            </>
          ) : (
            <Route path="/" element={<Login />} />
          )}
        </Routes>
      </div>
    </>
  );
};

export default App;
