import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import Login from "./Components/Login/Login";
import Sidebar from "./Components/Sidebar/Sidebar";
import Navbar from "./Components/Navbar/Navbar";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import Dashboard from "./Components/Dashboard/Dashboard";
import Posts from "./Components/Posts/Posts";
import Posts1 from "./Components/Posts/Posts1";
import Home from "./Home/Home";

function App() {
  const location = useLocation();
  const [showHeaderAndSidebar, setShowHeaderAndSidebar] = useState(false);

  useEffect(() => {
    // Check the route path and set the visibility of header and sidebar accordingly
    setShowHeaderAndSidebar(location.pathname === "/");
  }, [location.pathname]);

  return (
    <BrowserRouter>
      <div className="d-flex">
        {showHeaderAndSidebar && (
          <div className="w-auto">
            <Sidebar />
          </div>
        )}
        <div className="col">
          {showHeaderAndSidebar && <Navbar />}
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/Dashboard" element={<Dashboard />} />
            <Route path="/Posts" element={<Posts />} />
            <Route path="/Posts1" element={<Posts1 />} />
            <Route path="/Home" element={<Home />} />
          </Routes>
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
