import React, { useState } from 'react';

const Roles = () => {
  // Static roles data
  const [roles, setRoles] = useState([
    { roleName: "konkati.sravya@beeinnovations.com", status: "Active" },
    { roleName: "satish@mm", status: "Active" },
    { roleName: "md.abdul@beeinnovations.com", status: "Active" },
    { roleName: "rjsuneetha@beeinnovations.com", status: "Active" },
    { roleName: "nirisha.g@beeinnovations.com", status: "Active" },
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  // Filter roles based on search term
  const filteredRoles = roles.filter((role) =>
    role.roleName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Roles</h2>
        <button className="btn btn-primary" onClick={() => alert('Add Role clicked!')}>
          Add Role
        </button>
      </div>

      {/* Search bar */}
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search by role name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Roles table */}
      <table className="table table-striped">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Role Name</th>
            <th>Status</th>
            <th>Permissions</th>
          </tr>
        </thead>
        <tbody>
          {filteredRoles.map((role, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{role.roleName}</td>
              <td>{role.status}</td>
              <td>
                <button className="btn btn-secondary" onClick={() => alert('Permissions clicked!')}>
                  View Permissions
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Roles;
